var tip={show:function(title,content){var windowH=$(window).height(),windowW=$(window).width();$(".tip-bg").height(windowH<document.documentElement.scrollHeight?document.documentElement.scrollHeight:windowH);$(".tip-content").css({top:parseInt((windowH-$(".tip-content").height())/2)+"px",left:parseInt((windowW-$(".tip-content").width())/2)+"px"});$("#tipTitle").html(title);$("#tipContent").html(content);$(".tip-content, .tip-bg").show()},close:function(){$(".tip-content, .tip-bg").hide()}};var loadMore={init:function(url){$(window).scroll(function(){var $window=$(window),$list=$(".list:last");if($window.scrollTop()+$window.height()+100>$("body").height()){if($list.find(".loading").length===0){$list.append("<li onclick=\"loadMore.load('"+url+'\')" class="loading ft-gray last">查看更多...</li>');loadMore.load(url)}}})},load:function(url){$.ajax({url:url,type:"GET",cache:false,success:function(result,textStatus){if(result.sc){$(".list:last .loading").remove();var listHTML="";for(var i=0;i<10;i++){}$(".list:last").append(listHTML)}else{tip.show("加载失败",result.msg)}}})}};